import { useState } from "react";

// Reusable Select Input component
const SelectInput = ({ value, onChange, options, label }) => (
  <div>
    <label className="block text-sm font-medium mb-1">{label}</label>
    <select
      value={value}
      onChange={onChange}
      className="border rounded-lg px-4 py-2 w-full focus:ring"
    >
      {options.map((option) => (
        <option key={option.value} value={option.value}>
          {option.label}
        </option>
      ))}
    </select>
  </div>
);

// Reusable Date Input component
const DateInput = ({ value, onChange, label }) => (
  <div>
    <label className="block text-sm font-medium mb-1">{label}</label>
    <input
      type="date"
      value={value}
      onChange={onChange}
      className="border rounded-lg px-4 py-2 w-full focus:ring"
    />
  </div>
);

export default function FilterPanel({ consumers, postcodes, onApply, onReset }) {
  const [consumerId, setConsumerId] = useState("");
  const [postcode, setPostcode] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  const handleReset = () => {
    setConsumerId("");
    setPostcode("");
    setStartDate("");
    setEndDate("");
    onReset();
  };

  const handleApply = () => {
    onApply({ consumerId, postcode, startDate, endDate });
  };

  // Prepare options for selects
  const consumerOptions = [
    { value: "", label: "All Consumers" },
    ...consumers.map((c) => ({ value: c.Customer, label: `Consumer ${c.Customer}` })),
  ];

  const postcodeOptions = [
    { value: "", label: "All Postcodes" },
    ...consumers.map((c) => ({ value: c.postcode, label: `Consumer ${c.postcode}` })),
  ];

  return (
    <div className="bg-white rounded-xl shadow p-6 mb-6">
      <h2 className="text-lg font-medium mb-4">Filter Consumption Data</h2>
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <SelectInput
          value={consumerId}
          onChange={(e) => setConsumerId(e.target.value)}
          options={consumerOptions}
          label="Consumer"
        />

        <SelectInput
          value={postcode}
          onChange={(e) => setPostcode(e.target.value)}
          options={postcodeOptions}
          label="Postcode"
        />

        <DateInput value={startDate} onChange={(e) => setStartDate(e.target.value)} label="Start Date" />
        <DateInput value={endDate} onChange={(e) => setEndDate(e.target.value)} label="End Date" />
      </div>

      <div className="mt-4 flex justify-end space-x-2">
        <button onClick={handleReset} className="px-4 py-2 border rounded-lg hover:bg-gray-100">
          RESET
        </button>
        <button onClick={handleApply} className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">
          APPLY FILTERS
        </button>
      </div>
    </div>
  );
}
