import React, { useState, useMemo } from "react";
import Select from "react-select";
import { UserGroupIcon, MapPinIcon, CalendarIcon } from "@heroicons/react/24/outline";
import { useTheme } from "../context/ThemeContext";

export default function FilterPanel({ consumers, postcodes, onApply, onReset }) {
  const [consumerId, setConsumerId] = useState("");
  const [postcode, setPostcode] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const { darkMode } = useTheme();

  // Sort and memoize options
  const consumerOptions = useMemo(() => {
    return [
      { value: "", label: "All Consumers" },
      ...consumers
        .map(c => ({ value: c.Customer, label: `Consumer ${c.Customer}` }))
        .sort((a, b) => a.value - b.value)
    ];
  }, [consumers]);

  const postcodeOptions = useMemo(() => {
    return [
      { value: "", label: "All Postcodes" },
      ...postcodes
        .map(p => ({ value: p, label: `Postcode ${p}` }))
        .sort((a, b) => a.value - b.value)
    ];
  }, [postcodes]);

  const handleReset = () => {
    setConsumerId("");
    setPostcode("");
    setStartDate("");
    setEndDate("");
    onReset();
  };

  const handleApply = () => {
    onApply({
      consumerId: consumerId || undefined,
      postcode: postcode || undefined,
      startDate: startDate || undefined,
      endDate: endDate || undefined,
    });
  };

  // Shared react-select styles
  const selectStyles = {
    control: (base) => ({
      ...base,
      backgroundColor: darkMode ? '#374151' : '#fff',
      borderColor: darkMode ? '#4B5563' : '#D1D5DB',
      color: darkMode ? '#fff' : '#000'
    }),
    singleValue: (base) => ({ ...base, color: darkMode ? '#fff' : '#000' }),
    input: (base) => ({ ...base, color: darkMode ? '#fff' : '#000' }),
    menu: (base) => ({ ...base, backgroundColor: darkMode ? '#374151' : '#fff' }),
    option: (base, state) => ({
      ...base,
      backgroundColor: state.isFocused
        ? (darkMode ? '#4B5563' : '#E5E7EB')
        : (darkMode ? '#374151' : '#fff'),
      color: darkMode ? '#fff' : '#000',
    }),
  };

  return (
    <div className={`rounded-xl shadow p-6 mb-6 ${darkMode ? 'bg-gray-800 text-white' : 'bg-white text-black'}`}>
      <h2 className="text-lg font-medium mb-4">Filter Consumption Data</h2>
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        {/* Consumer Select */}
        <div className="flex flex-col">
          <label className="flex items-center gap-2 mb-2">
            <UserGroupIcon className="h-5 w-5 text-blue-600" />
            <span>Consumer</span>
          </label>
          <Select
            options={consumerOptions}
            styles={selectStyles}
            isClearable
            isSearchable
            placeholder="All Consumers"
            onChange={(opt) => setConsumerId(opt?.value ?? "")}
            value={consumerOptions.find(o => o.value === consumerId)}
          />
        </div>

        {/* Postcode Select */}
        <div className="flex flex-col">
          <label className="flex items-center gap-2 mb-2">
            <MapPinIcon className="h-5 w-5 text-green-600" />
            <span>Postcode</span>
          </label>
          <Select
            options={postcodeOptions}
            styles={selectStyles}
            isClearable
            isSearchable
            placeholder="All Postcodes"
            onChange={(opt) => setPostcode(opt?.value ?? "")}
            value={postcodeOptions.find(o => o.value === postcode)}
          />
        </div>

        {/* Start Date */}
        <div className="flex flex-col">
          <label className="flex items-center gap-2 mb-2">
            <CalendarIcon className="h-5 w-5 text-pink-600" />
            <span>Start Date</span>
          </label>
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            className={`border rounded-lg px-4 py-2 focus:ring ${darkMode ? 'bg-gray-700 text-white border-gray-600' : ''}`}
          />
        </div>

        {/* End Date */}
        <div className="flex flex-col">
          <label className="flex items-center gap-2 mb-2">
            <CalendarIcon className="h-5 w-5 text-yellow-600" />
            <span>End Date</span>
          </label>
          <input
            type="date"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            className={`border rounded-lg px-4 py-2 focus:ring ${darkMode ? 'bg-gray-700 text-white border-gray-600' : ''}`}
          />
        </div>
      </div>

      <div className="mt-4 flex justify-end space-x-2">
        <button
          onClick={handleReset}
          className={`px-4 py-2 border rounded-lg hover:bg-gray-100 transition ${darkMode ? 'border-gray-600 text-white hover:bg-gray-700' : ''}`}
        >
          RESET
        </button>
        <button
          onClick={handleApply}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
        >
          APPLY FILTERS
        </button>
      </div>
    </div>
  );
}
