import {
  HomeModernIcon,
  UserGroupIcon,
  BellIcon,
  CogIcon,
  ArrowRightEndOnRectangleIcon,
} from "@heroicons/react/24/outline";

export default function Sidebar() {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login"); // Or whatever your login route is
  };
  return (
    <aside className="fixed left-0 top-0 h-screen w-64 bg-gray-900 text-gray-200 flex flex-col">
      <div className="px-6 py-8 flex items-center">
        <span className="text-2xl font-bold">âš¡ Ausgrid</span>
      </div>

      {/* Scrollable nav */}
      <nav className="flex-1 px-4 space-y-1 overflow-y-auto">
        {[
          { name: "Dashboard", icon: HomeModernIcon },
          { name: "Consumers List", icon: UserGroupIcon },
          { name: "Alerts", icon: BellIcon },
          { name: "Profile", icon: CogIcon },
        ].map((item) => (
          <a
            key={item.name}
            href="#"
            className="flex items-center px-3 py-2 rounded hover:bg-gray-800"
          >
            <item.icon className="h-5 w-5 mr-3" />
            {item.name}
          </a>
        ))}
      </nav>

      {/* Sticky logout button */}
      <button
          onClick={handleLogout}
          className="flex items-center w-full px-3 py-2 rounded hover:bg-gray-800"
        >
          <ArrowRightEndOnRectangleIcon className="h-5 w-5 mr-3" />
          Logout
        </button>
    </aside>
  );
}
