import Sidebar from "../components/Sidebar";
import Header from "../components/Header";
import FilterPanel from "../components/FilterPanel";
import StatCard from "../components/StatCard";
import ChartCard from "../components/ChartCard";

import {
  UsersIcon,
  LocationMarkerIcon,
  CalendarIcon,
} from "@heroicons/react/outline";

export default function Dashboard() {
  const handleApply = () => {/* fetch & update stats/charts */};
  const handleReset = () => {/* reset filters */};

  return (
    <div className="flex min-h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        <Header />
        <main className="p-6 overflow-auto">
          <FilterPanel onApply={handleApply} onReset={handleReset} />

          {/* Stats Row */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
            <StatCard
              icon={<UsersIcon className="h-6 w-6 text-blue-600" />}
              label="Consumer ID"
              value="All"
            />
            <StatCard
              icon={<LocationMarkerIcon className="h-6 w-6 text-blue-600" />}
              label="Postcode"
              value="All"
            />
            <StatCard
              icon={<CalendarIcon className="h-6 w-6 text-blue-600" />}
              label="Start Date"
              value="All"
            />
            <StatCard
              icon={<CalendarIcon className="h-6 w-6 text-blue-600" />}
              label="End Date"
              value="All"
            />
          </div>

          {/* Charts Row */}
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            <ChartCard>
              {/* <YearlyBarChart data={...} /> */}
            </ChartCard>
            <ChartCard>
              {/* <MonthlyLineChart data={...} /> */}
            </ChartCard>
            <ChartCard>
              {/* <DailyLineChart data={...} /> */}
            </ChartCard>
          </div>
        </main>
      </div>
    </div>
  );
}
