import { useState } from "react";
import { UserGroupIcon, MapPinIcon, CalendarIcon } from "@heroicons/react/24/outline";
import { useTheme } from "../context/ThemeContext";

// Reusable Select Input component
const SelectInput = ({ value, onChange, options, label, icon, iconBg, darkMode }) => (
  <div className={`flex flex-col rounded-xl shadow p-4 min-h-[110px] justify-between ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
    <div className="flex items-center gap-4">
      <span className={`rounded-lg p-3 ${iconBg} flex items-center justify-center h-12 w-12`}>
        {icon}
      </span>
      <div className="flex flex-col justify-center">
        <span className={`text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-500'}`}>{label}</span>
        <span className={`text-xl font-bold ${darkMode ? 'text-white' : 'text-black'}`}>
          {value ? options.find((o) => o.value === value)?.label || value : "All"}
        </span>
      </div>
    </div>
    <select
      value={value}
      onChange={onChange}
      className={`mt-3 border rounded-lg px-4 py-2 w-full focus:ring ${darkMode ? 'bg-gray-700 text-white border-gray-600' : ''}`}
    >
      {options.map((option) => (
        <option key={option.value} value={option.value}>
          {option.label}
        </option>
      ))}
    </select>
  </div>
);

// Reusable Date Input component
const DateInput = ({ value, onChange, label, icon, iconBg, darkMode }) => (
  <div className={`flex flex-col rounded-xl shadow p-4 min-h-[110px] justify-between ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
    <div className="flex items-center gap-4">
      <span className={`rounded-lg p-3 ${iconBg} flex items-center justify-center h-12 w-12`}>
        {icon}
      </span>
      <div className="flex flex-col justify-center">
        <span className={`text-sm font-medium mb-1 ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>{label}</span>
        <span className={`text-xl font-bold ${darkMode ? 'text-white' : 'text-gray-900'}`}>{value ? value : "All"}</span>
      </div>
    </div>
    <input
      type="date"
      value={value}
      onChange={onChange}
      className={`mt-3 border rounded-lg px-4 py-2 w-full focus:ring ${darkMode ? 'bg-gray-700 text-white border-gray-600' : ''}`}
    />
  </div>
);

export default function FilterPanel({ consumers, postcodes, onApply, onReset }) {
  const [consumerId, setConsumerId] = useState("");
  const [postcode, setPostcode] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const { darkMode } = useTheme();

  const handleReset = () => {
    setConsumerId("");
    setPostcode("");
    setStartDate("");
    setEndDate("");
    onReset();
  };

  const handleApply = () => {
    onApply({ consumerId, postcode, startDate, endDate });
  };

  // Prepare options for selects
  const consumerOptions = [
    { value: "", label: "All Consumers" },
    ...consumers.map((c) => ({ value: c.Customer, label: `Consumer ${c.Customer}` })),
  ];

  const postcodeOptions = [
    { value: "", label: "All Postcodes" },
    ...consumers.map((c) => ({ value: c.Postcode, label: `Postcode ${c.Postcode}` })),
  ];

  return (
    <div className={`rounded-xl shadow p-6 mb-6 ${darkMode ? 'bg-gray-800 text-white' : 'bg-white'}`}>
      <h2 className={`text-lg font-medium mb-4 ${darkMode ? 'text-white' : ''}`}>Filter Consumption Data</h2>
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <SelectInput
          value={consumerId}
          onChange={(e) => setConsumerId(e.target.value)}
          options={consumerOptions}
          label="Consumer ID"
          icon={<UserGroupIcon className="h-6 w-6 text-blue-600" />}
          iconBg="bg-blue-100"
          darkMode={darkMode}
        />

        <SelectInput
          value={postcode}
          onChange={(e) => setPostcode(e.target.value)}
          options={postcodeOptions}
          label="Postcode"
          icon={<MapPinIcon className="h-6 w-6 text-green-600" />}
          iconBg="bg-green-100"
          darkMode={darkMode}
        />

        <DateInput
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
          label="Start Date"
          icon={<CalendarIcon className="h-6 w-6 text-pink-600" />}
          iconBg="bg-pink-100"
          darkMode={darkMode}
        />
        <DateInput
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
          label="End Date"
          icon={<CalendarIcon className="h-6 w-6 text-yellow-600" />}
          iconBg="bg-yellow-100"
          darkMode={darkMode}
        />
      </div>

      <div className="mt-4 flex justify-end space-x-2">
        <button onClick={handleReset} className={`px-4 py-2 border rounded-lg transition-all duration-200 hover:shadow-[0_0_12px_4px_rgba(107,114,128,0.5)] focus:outline-none ${darkMode ? 'border-gray-600 text-white hover:bg-gray-700' : ''}`}>
          RESET
        </button>
        <button onClick={handleApply} className="px-4 py-2 bg-blue-600 text-white rounded-lg transition-all duration-200 hover:shadow-[0_0_12px_4px_rgba(59,130,246,0.7)] focus:outline-none">
          APPLY FILTERS
        </button>
      </div>
    </div>
  );
}
