import React, { useEffect, useState } from "react";
import Sidebar from "../components/Sidebar";
import Header from "../components/Header";
import FilterPanel from "../components/FilterPanel";
import StatCard from "../components/StatCard";
import ChartCard from "../components/ChartCard";
import { fetchConsumers, fetchRecords } from "../services/api";
import {
  UsersIcon,
  MapPinIcon,
  CalendarIcon,
} from "@heroicons/react/24/outline";

export default function Dashboard() {
  const [consumers, setConsumers] = useState([]);
  const [postcodes, setPostcodes] = useState([]);
  const [records, setRecords] = useState([]);
  const [stats, setStats] = useState({
    consumerId: "All",
    postcode: "All",
    startDate: "All",
    endDate: "All",
  });

  useEffect(() => {
    fetchRecords({}) // empty filter = all records
      .then((data) => {
        setRecords(data);
        // initialize stats to “All” with real dates if you like
        setStats({
          consumerId: "All",
          postcode: "All",
          startDate: "All",
          endDate: "All",
        });
      });
  }, []);
  
  // Load consumers once
  useEffect(() => {
    fetchConsumers().then(setConsumers);
  }, []);

  // Update postcodes when records change
  useEffect(() => {
    const pcs = [...new Set(records.map((r) => r.Postcode))];
    setPostcodes(pcs);
  }, [records]);

  // Apply filters
  const handleApply = ({ consumerId, postcode, startDate, endDate }) => {
    fetchRecords({ consumerId, postcode, startDate, endDate }).then((data) => {
      setRecords(data);  // Update records state
      setStats({
        consumerId: consumerId || "All",
        postcode: postcode || "All",
        startDate: startDate || "All",
        endDate: endDate || "All",
      });
    });
  };

  // Reset filters
  const handleReset = () => {
    setRecords([]);
    setStats({
      consumerId: "All",
      postcode: "All",
      startDate: "All",
      endDate: "All",
    });
    setPostcodes([]);
    fetchRecords({}).then((data) => {
      setRecords(data);
      setStats({
        consumerId: "All",
        postcode: "All",
        startDate: "All",
        endDate: "All",
      });
    });
  };

  // Helper function to aggregate data for charts
  const aggregateData = (records, period) => {
    const result = { labels: [], values: [] };

    records.forEach((record) => {
      let label = "";
      const date = new Date(record.date);

      if (period === "year") {
        label = date.getFullYear();
      } else if (period === "month") {
        label = `${date.getMonth() + 1}-${date.getFullYear()}`;
      } else if (period === "day") {
        label = date.toLocaleDateString();
      }

      if (!result.labels.includes(label)) {
        result.labels.push(label);
        result.values.push(record.consumption);
      } else {
        const index = result.labels.indexOf(label);
        result.values[index] += record.consumption;
      }
    });

    return result;
  };

  const yearlyData = aggregateData(records, "year");
  const monthlyData = aggregateData(records, "month");
  const dailyData = aggregateData(records, "day");

  return (
    <div className="flex min-h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        <Header />

        <main className="p-6 overflow-auto">
          <FilterPanel
            consumers={consumers}
            postcodes={postcodes}
            onApply={handleApply}
            onReset={handleReset}
          />

          {/* Stats */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
            <StatCard
              icon={<UsersIcon className="h-6 w-6 text-blue-600" />}
              label="Consumer ID"
              value={stats.consumerId}
            />
            <StatCard
              icon={<MapPinIcon className="h-6 w-6 text-blue-600" />}
              label="Postcode"
              value={stats.postcode}
            />
            <StatCard
              icon={<CalendarIcon className="h-6 w-6 text-blue-600" />}
              label="Start Date"
              value={stats.startDate}
            />
            <StatCard
              icon={<CalendarIcon className="h-6 w-6 text-blue-600" />}
              label="End Date"
              value={stats.endDate}
            />
          </div>

          {/* Records Table */}
          <div className="bg-white shadow rounded-lg mb-6 overflow-x-auto">
            <table className="w-full table-auto text-left">
              <thead>
                <tr className="border-b bg-gray-50">
                  <th className="p-3">Date</th>
                  <th className="p-3">Consumption</th>
                  <th className="p-3">Postcode</th>
                </tr>
              </thead>
              <tbody>
                {records.map((r) => (
                  <tr
                    key={`${r.Customer}-${r.date}`}
                    className="hover:bg-gray-100"
                  >
                    <td className="p-3">
                      {new Date(r.date).toLocaleDateString()}
                    </td>
                    <td className="p-3">{r.consumption}</td>
                    <td className="p-3">{r.Postcode}</td>
                  </tr>
                ))}
                {!records.length && (
                  <tr>
                    <td colSpan="3" className="p-3 text-center text-gray-500">
                      No records to display
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>

          {/* Charts */}
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            <ChartCard title="Yearly Consumption" data={yearlyData} />
            <ChartCard title="Monthly Consumption" data={monthlyData} />
            <ChartCard title="Daily Consumption" data={dailyData} />
          </div>
        </main>
      </div>
    </div>
  );
}
