import React from "react";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from "chart.js";
import zoomPlugin from "chartjs-plugin-zoom";
import { useTheme } from "../context/ThemeContext";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, zoomPlugin);

const ChartCard = ({ title, data }) => {
  const { darkMode } = useTheme();

  const chartData = {
    labels: data.labels,
    datasets: [
      {
        label: title,
        data: data.values,
        fill: true,
        backgroundColor: (context) => {
          const ctx = context.chart.ctx;
          const gradient = ctx.createLinearGradient(0, 0, 0, 400);
          if (darkMode) {
            gradient.addColorStop(0, "rgba(255,255,255,0.08)");
            gradient.addColorStop(1, "rgba(255,255,255,0.01)");
          } else {
            gradient.addColorStop(0, "rgba(255,255,255,0.15)");
            gradient.addColorStop(1, "rgba(255,255,255,0.02)");
          }
          return gradient;
        },
        borderColor: darkMode ? "rgba(255,255,255,0.6)" : "#fff",
        pointBackgroundColor: darkMode ? "rgba(255,255,255,0.6)" : "#fff",
        pointBorderColor: darkMode ? "rgba(255,255,255,0.6)" : "#fff",
        borderWidth: 2,
        pointRadius: 4,
        pointHoverRadius: 6,
        tension: 0.4,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: { display: false },
      tooltip: {
        enabled: true,
        mode: "index",
        intersect: false,
        callbacks: {
          label: (tooltipItem) => `${tooltipItem.dataset.label}: ${tooltipItem.raw}`,
        },
        backgroundColor: "#222",
        titleColor: "#fff",
        bodyColor: "#fff",
        borderColor: "rgba(255,255,255,0.7)",
        borderWidth: 1,
      },
      zoom: {
        pan: {
          enabled: true,
          mode: "xy",
        },
        zoom: {
          wheel: {
            enabled: true,
          },
          pinch: {
            enabled: true,
          },
          mode: "xy",
        },
        limits: {
          x: { minRange: 1 },
          y: { minRange: 1 },
        },
      },
    },
    scales: {
      x: {
        grid: { display: false },
        ticks: {
          color: darkMode ? "#fff" : "#000", // Dynamic text color for X-axis ticks
          font: { size: 12 },
        },
      },
      y: {
        grid: { display: false },
        ticks: {
          color: darkMode ? "#fff" : "#000", // Dynamic text color for Y-axis ticks
          font: { size: 12 },
        },
      },
    },
  };

  return (
    <div
      className={`rounded-xl shadow-lg p-4 flex flex-col items-center justify-center min-w-[220px] max-w-[350px] min-h-[260px] max-h-[320px] w-full h-full transition-colors duration-300`}
      style={{ backgroundColor: "rgba(185, 185, 185, 0.75)" }} // Apply thergb(0, 0, 0) color here
    >
      <h3 className="text-white text-lg font-semibold mb-2 text-center">{title}</h3>
      <div className="w-full h-48 md:h-40 lg:h-48 xl:h-56">
        <Line data={chartData} options={chartOptions} />
      </div>
    </div>
  );
};

export default ChartCard;
