// src/services/api.js
import { API } from "./api";  
// … your existing interceptors and fetchConsumers/fetchRecords …

// New predictConsumption:
export const predictConsumption = async ({ consumerId, month }) => {
  const { data } = await API.post(
    "/predict/", 
    { consumerId, month }
  );
  // expect { predictions: { labels: [...], values: [...] } }
  return data.predictions;
};
