import React, { useEffect, useState } from "react";

const FilterPanel = ({ consumers, postcodes, onApply, onReset }) => {
  const [selectedConsumer, setSelectedConsumer] = useState("All");
  const [selectedPostcode, setSelectedPostcode] = useState("All");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  // Handle consumer and postcode changes
  useEffect(() => {
    if (consumers.length) {
      setSelectedConsumer(consumers[0].id);  // Default to first consumer if available
    }
    if (postcodes.length) {
      setSelectedPostcode(postcodes[0]);  // Default to first postcode if available
    }
  }, [consumers, postcodes]);

  // Handle Apply button
  const handleApply = () => {
    onApply({
      consumerId: selectedConsumer,
      postcode: selectedPostcode,
      startDate,
      endDate,
    });
  };

  // Handle Reset button
  const handleReset = () => {
    setSelectedConsumer("All");
    setSelectedPostcode("All");
    setStartDate("");
    setEndDate("");
    onReset();
  };

  return (
    <div className="bg-white shadow rounded-lg p-6 mb-6">
      <h3 className="text-xl font-semibold mb-4">Filters</h3>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        <div>
          <label className="block text-sm font-medium text-gray-700">Consumer</label>
          <select
            value={selectedConsumer}
            onChange={(e) => setSelectedConsumer(e.target.value)}
            className="mt-2 block w-full px-3 py-2 border border-gray-300 rounded-md"
          >
            <option value="All">All Consumers</option>
            {consumers.map((consumer) => (
              <option key={consumer.id} value={consumer.id}>
                {consumer.name}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">Postcode</label>
          <select
            value={selectedPostcode}
            onChange={(e) => setSelectedPostcode(e.target.value)}
            className="mt-2 block w-full px-3 py-2 border border-gray-300 rounded-md"
          >
            <option value="All">All Postcodes</option>
            {postcodes.map((postcode, index) => (
              <option key={index} value={postcode}>
                {postcode}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">Start Date</label>
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            className="mt-2 block w-full px-3 py-2 border border-gray-300 rounded-md"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">End Date</label>
          <input
            type="date"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            className="mt-2 block w-full px-3 py-2 border border-gray-300 rounded-md"
          />
        </div>
      </div>

      <div className="mt-4 flex justify-end gap-4">
        <button
          onClick={handleReset}
          className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-200 hover:bg-gray-300 rounded-md"
        >
          Reset
        </button>
        <button
          onClick={handleApply}
          className="px-4 py-2 text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 rounded-md"
        >
          Apply Filters
        </button>
      </div>
    </div>
  );
};

export default FilterPanel;
