import { useState } from "react";

export default function FilterPanel({ consumers, postcodes, onApply, onReset }) {
  const [consumerId, setConsumerId] = useState("");
  const [postcode,   setPostcode]   = useState("");
  const [startDate,  setStartDate]  = useState("");
  const [endDate,    setEndDate]    = useState("");

  return (
    <div className="bg-white rounded-xl shadow p-6 mb-6">
      <h2 className="text-lg font-medium mb-4">Filter Consumption Data</h2>
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        {/* Consumer Select */}
        <select
          value={consumerId}
          onChange={(e) => setConsumerId(e.target.value)}
          className="border rounded-lg px-4 py-2 focus:ring"
        >
          <option value="">All Consumers</option>
          {consumers.map((c) => (
            <option key={c.Customer} value={c.Customer}>
              Consumer {c.Customer}
            </option>
          ))}
        </select>

        {/* Postcode Select */}
        <select
          value={postcode}
          onChange={(e) => setPostcode(e.target.value)}
          className="border rounded-lg px-4 py-2 focus:ring"
        >
          <option value="">All Postcodes</option>
          {postcodes.map((p) => (
            <option key={p} value={p}>
              {p}
            </option>
          ))}
        </select>

        {/* Start Date */}
        <input
          type="date"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
          className="border rounded-lg px-4 py-2 focus:ring"
        />

        {/* End Date */}
        <input
          type="date"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
          className="border rounded-lg px-4 py-2 focus:ring"
        />
      </div>

      <div className="mt-4 flex justify-end space-x-2">
        <button
          onClick={() => {
            setConsumerId("");
            setPostcode("");
            setStartDate("");
            setEndDate("");
            onReset();
          }}
          className="px-4 py-2 border rounded-lg hover:bg-gray-100"
        >
          RESET
        </button>
        <button
          onClick={() =>
            onApply({ consumerId, postcode, startDate, endDate })
          }
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
        >
          APPLY FILTERS
        </button>
      </div>
    </div>
  );
}
