import React, { useState } from 'react';
import axios from 'axios';

const FilterPanel = ({ onDataFetched, token }) => {
  const [consumerId, setConsumerId] = useState('');
  const [postcode, setPostcode] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  const handleFilter = async () => {
    let url = '/api/records/';
    const params = new URLSearchParams();

    if (consumerId) params.append('Customer', consumerId);
    if (postcode) params.append('Postcode', postcode);
    if (startDate) params.append('start_date', startDate);
    if (endDate) params.append('end_date', endDate);

    if (params.toString()) url += `?${params.toString()}`;

    try {
      const response = await axios.get(url, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      console.log('Filtered data:', response.data);
      onDataFetched(response.data); // Pass to parent for chart update
    } catch (error) {
      console.error('Error loading data:', error);
    }
  };

  return (
    <div className="p-4 rounded bg-white shadow-md grid gap-4 grid-cols-2 md:grid-cols-4">
      <input
        type="text"
        placeholder="Consumer ID"
        value={consumerId}
        onChange={(e) => setConsumerId(e.target.value)}
        className="border rounded p-2"
      />
      <input
        type="text"
        placeholder="Postcode"
        value={postcode}
        onChange={(e) => setPostcode(e.target.value)}
        className="border rounded p-2"
      />
      <input
        type="date"
        value={startDate}
        onChange={(e) => setStartDate(e.target.value)}
        className="border rounded p-2"
      />
      <input
        type="date"
        value={endDate}
        onChange={(e) => setEndDate(e.target.value)}
        className="border rounded p-2"
      />
      <button
        onClick={handleFilter}
        className="col-span-2 md:col-span-1 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
      >
        Apply Filters
      </button>
    </div>
  );
};

export default FilterPanel;
