import axios from "axios";

// Base URL of your Django API
const API = axios.create({
  baseURL: "http://localhost:8000/api",
});

// Fetch all consumers
export const fetchConsumers = () =>
  API.get("/consumers/", {
    headers: { Authorization: `Bearer ${localStorage.getItem("jwt_token")}` },
  }).then((res) => res.data);

// Fetch records (optionally filtered)
export const fetchRecords = ({ consumerId, postcode, startDate, endDate } = {}) => {
  const params = {};
  if (consumerId) params.Customer   = consumerId;
  if (postcode)    params.Postcode   = postcode;
  if (startDate)   params.start_date = startDate;
  if (endDate)     params.end_date   = endDate;

  return API.get("/records/", {
    headers: { Authorization: `Bearer ${localStorage.getItem("jwt_token")}` },
    params,
  }).then((res) => res.data);
};
